mapper.select:
select cart_pk AS cartPk, new_book_image AS image, c.new_book_pk As bookPk, new_book_title AS title, cart_quantity AS quantity, 
	new_book_price AS price, new_book_mount AS mount
from cart c
	join new_book b on c.new_book_pk = b.new_book_pk
where c.user_pk = #{userPk}
ORDER BY c.cart_Pk DESC;

select cart_pk AS cartPk, new_book_image AS image, c.new_book_pk As bookPk, new_book_title AS title, cart_quantity AS quantity, new_book_price AS price, new_book_mount AS mount from cart c join new_book b on c.new_book_pk = b.new_book_pk WHERE c.cart_pk IN
#{cartPk}
ORDER BY c.cart_Pk DESC;

 		select *
		from products p
			join bill b on b.bill_pk = p.bill_pk
			join new_book n on n.new_book_pk = p.new_book_pk
		where b.user_pk=#{userPk}
		ORDER BY b.bill_pk DESC;
